import React, { Component } from "react";
import { Row, Col, Button, CardColumns, Card } from "react-bootstrap";
import "../css/list.css";

import { connect } from "react-redux";

class searchList extends Component {
    constructor(props) {
        super(props)
        this.state = {
            view: false,
            search: null

        };
    }
    componentDidMount() {

        const { search } = this.props
        let getItem = localStorage.getItem('searchVal');
        if (getItem) {
            let clone = JSON.parse(getItem)
             console.log(getItem, 'kx');

            this.setState({ search: clone, view: true })
        }
    }



    render() {
        const { search, view } = this.state;
        // console.log(search, '--454545');

        return (
            <CardColumns id="card-columns">
                {
                    view ?
                        <Card >
                            <Card.Img
                                variant="top"
                                src={`https://image.tmdb.org/t/p/w185${search.backdrop_path}`}
                            />
                            <Card.Body>
                                <Card.Title>
                                    <h4>{search.title}</h4>
                                    <h6>{search.vote_average || "No Rating"} </h6>
                                </Card.Title>
                                <Card.Text>{search.overview}</Card.Text>
                            </Card.Body>
                            <Card.Footer>
                                <Row>
                                    <Col sm>
                                        {/* <Button onClick={() => this.setItem(!isFavor ? search : search.id)} variant="danger">
                                                <span className={isFavor ? "glyphicon glyphicon-heart-empty" : "glyphicon glyphicon-heart"} />
                                            </Button> */}
                                    </Col>
                                    <Col sm />
                                    <Col sm align="right">
                                        <Button variant="danger">
                                            <span className="glyphicon glyphicon-plus" />
                                        </Button>
                                    </Col>
                                </Row>
                            </Card.Footer>
                        </Card>

                        :
                        <div>
                            <text>no search Found</text>
                        </div>
                }
            </CardColumns>
        );
    }
}

const mapStateToProps = state => {
    return {
        search: state.movies.search,
    };
};

const mapDispatchToProps = dispatch => {
    return {
        // getMovies: page => dispatch(getMovies(page)),
        // addFavorites: id => dispatch(addFavorites(id))
    };
};

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(searchList);
