import React, { Component } from 'react';
import { Navbar, Nav } from 'react-bootstrap';
import Search from './Search.js'
import searchList from './SearchList'
import { searchData } from "../actions/movieActions";
import { connect } from "react-redux";

class Header extends Component {
  constructor(props){
    super(props);
    this.state = {
      searchList: {}
    }
  }

SearchMovieList(){
  // console.log(this.state.searchList, '******///')
        this.props.searchData(this.state.searchList);
        this.props.navigateToSearch()
}

  render() {
    return (
      <Navbar collapseOnSelect bg="light" variant="light" sticky="top">
        <Navbar.Brand href="/">Rimbo Cinema</Navbar.Brand>
        <Nav className="mr-auto">
          <Nav.Link href="/favorites">Favorites</Nav.Link>
          <Nav.Link href="/add">My List</Nav.Link>
        </Nav>
        <Search onClickSearch={(ev) => this.setState({searchList: ev})}  />
        <Nav.Link onClick={() => this.SearchMovieList()} >Search</Nav.Link>
      </Navbar>
    );
  }
}

const mapDispatchToProps = dispatch => {
  return {
    searchData: val => dispatch(searchData(val)),
  };
};

export default connect(
  null,
  mapDispatchToProps
)(Header);
