import React, { Component } from "react";
import { Row, Col, Button, CardColumns, Card } from "react-bootstrap";
import "../css/list.css";
import Header from './Header.js'
import { connect } from "react-redux";

class searchList extends Component {
    constructor(props) {
        super(props)
        this.state = {
            view: false,
            search: null

        };
    }


    componentWillMount() {
        const { search } = this.props;
        if (search && search.title) {
            this.setState({ search: search, view: true })
        }
    }

    componentWillReceiveProps(props) {
        const { search } = props
        if (search && search.title) {
            this.setState({ search: search, view: true })
        }
    }


    setItem = (movie) => {
        let getItem = localStorage.getItem('favourite');
        if (getItem) {
            let cloneArr = JSON.parse(getItem);
            if (movie && movie.title) {
                cloneArr.push(movie);
                localStorage.setItem('favourite', JSON.stringify(cloneArr))
                this.forceUpdate();
            } else {
                cloneArr.map((item, index) => {
                    if (item.id === movie) {
                        cloneArr.splice(index, 1)
                        localStorage.setItem('favourite', JSON.stringify(cloneArr))
                        this.forceUpdate();
                    }
                })
            }
        }
        else {
            let arr = [movie];
            localStorage.setItem('favourite', JSON.stringify(arr));
            this.forceUpdate();
        }
    }

    add(movie) {
        let getItem = localStorage.getItem('AddLike');
        if (getItem) {
            let cloneArr = JSON.parse(getItem);
            if (movie && movie.title) {
                cloneArr.push(movie);
                localStorage.setItem('AddLike', JSON.stringify(cloneArr))
                this.forceUpdate();
            } else {
                cloneArr.map((item, index) => {
                    if (item.id === movie) {
                        cloneArr.splice(index, 1)
                        localStorage.setItem('AddLike', JSON.stringify(cloneArr))
                        this.forceUpdate();
                    }
                })
            }
        }
        else {
            let arr = [movie];
            localStorage.setItem('AddLike', JSON.stringify(arr));
            this.forceUpdate();
        }
    }


    render() {
        const { search, view } = this.state;

        var isFavor = view && JSON.parse(localStorage.getItem('favourite')) ? JSON.parse(localStorage.getItem('favourite')).find(arr => arr.id === search.id) : false
        var isLike = view && JSON.parse(localStorage.getItem('AddLike')) ? JSON.parse(localStorage.getItem('AddLike')).find(arr => arr.id === search.id) : false

        return (
            <div>
                <Header navigateToSearch={() => this.props.history.push('/search')} />
                <CardColumns id="card-columns">
                    {
                        view ?
                            <Card >
                                <Card.Img
                                    variant="top"
                                    src={`https://image.tmdb.org/t/p/w185${search.backdrop_path}`}
                                />
                                <Card.Body>
                                    <Card.Title>
                                        <h4>{search.title}</h4>
                                        <h6>{search.vote_average || "No Rating"} </h6>
                                    </Card.Title>
                                    <Card.Text>{search.overview}</Card.Text>
                                </Card.Body>
                                <Card.Footer>
                                    <Row>
                                        <Col sm>
                                            <Button onClick={() => this.setItem(!isFavor ? search : search.id)} variant="danger">
                                                <span className={isFavor ? "glyphicon glyphicon-heart-empty" : "glyphicon glyphicon-heart"} />
                                            </Button>
                                        </Col>
                                        <Col sm />
                                        <Col sm align="right">
                                            <Button onClick={() => this.add(!isLike ? search : search.id)} variant="danger">
                                                <span className={isLike ? "glyphicon glyphicon-minus" : "glyphicon glyphicon-plus"} />
                                            </Button>
                                        </Col>
                                    </Row>
                                </Card.Footer>
                            </Card>

                            :
                            <div>
                                <text>no search Found</text>
                            </div>
                    }
                </CardColumns>
            </div>
        );
    }
}

const mapStateToProps = state => {
    return {
        search: state.movies.search,
    };
};

const mapDispatchToProps = dispatch => {
    return {
        // getMovies: page => dispatch(getMovies(page)),
        // addFavorites: id => dispatch(addFavorites(id))
    };
};

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(searchList);
