import React, { Component } from "react";
import { Row, Col, Button, CardColumns, Card } from "react-bootstrap";
import "../css/list.css";
import { getMovies } from "../actions";
import { addFavorites, removeFavorites } from "../actions/movieActions";
import Header from './Header.js'
import { connect } from "react-redux";


class AddList extends Component {
    state = {
        movies: [],
        searchInfo: {},
        page: 1,
        onBOttom: false,

    };

    componentDidMount() {
        let getItem = localStorage.getItem('AddLike');
        if (getItem) {
            let cloneArr = JSON.parse(getItem);
            this.setState({ favorite: cloneArr })
        }
    }


    setItem = (movie) => {
        let getItem = localStorage.getItem('favourite');
        if (getItem) {
            let cloneArr = JSON.parse(getItem);
            if (movie && movie.name) {
                cloneArr.push(movie);
                localStorage.setItem('favourite', JSON.stringify(cloneArr))
                this.forceUpdate();
            } else {
                cloneArr.map((item, index) => {
                    if (item.id === movie) {
                        cloneArr.splice(index, 1)
                        localStorage.setItem('favourite', JSON.stringify(cloneArr))
                        this.forceUpdate();
                    }
                })
            }
        }
        else {
            let arr = [movie];
            localStorage.setItem('favourite', JSON.stringify(arr));
            this.forceUpdate();
        }
    }

    add(movie) {
        let getItem = localStorage.getItem('AddLike');
        if (getItem) {
            let cloneArr = JSON.parse(getItem);
            if (movie && movie.name) {
                cloneArr.push(movie);
                localStorage.setItem('AddLike', JSON.stringify(cloneArr))
                this.forceUpdate();
            } else {
                cloneArr.map((item, index) => {
                    if (item.id === movie) {
                        cloneArr.splice(index, 1)
                        localStorage.setItem('AddLike', JSON.stringify(cloneArr))
                        this.forceUpdate();
                        this.setState({ favorite: cloneArr })
              }
            })
          }
        }
        else {
          let arr = [movie];
          localStorage.setItem('AddLike', JSON.stringify(arr));
          this.forceUpdate();
        }
      }


    render() {
        const { favorite } = this.state;

        return (
            <div>
                <Header navigateToSearch={() => this.props.history.push('/search')} />
                <CardColumns id="card-columns">
                    {
                        favorite ?
                            favorite.map((movie, index) => {
                                const isFavor = JSON.parse(localStorage.getItem('favourite')) ? JSON.parse(localStorage.getItem('favourite')).find(arr => arr.id === movie.id) : false
                                const isLike = JSON.parse(localStorage.getItem('AddLike')) ? JSON.parse(localStorage.getItem('AddLike')).find(arr => arr.id === movie.id) : false

                                // console.log(isFavor,"lalalal")
                                return (
                                    <Card key={movie.id}>
                                        <Card.Img
                                            variant="top"
                                            src={`https://image.tmdb.org/t/p/w185${movie.backdrop_path}`}
                                        />
                                        <Card.Body>
                                            <Card.Title>
                                                <h4>{movie.name}</h4>
                                                <h6>{movie.vote_average || "No Rating"} </h6>
                                            </Card.Title>
                                            <Card.Text>{movie.overview}</Card.Text>
                                        </Card.Body>
                                        <Card.Footer>
                                            <Row>
                                                <Col sm>
                                                    <Button onClick={() => this.setItem(!isFavor ? movie : movie.id)} variant="danger">
                                                        <span className={isFavor ? "glyphicon glyphicon-heart-empty" : "glyphicon glyphicon-heart"} />
                                                    </Button>
                                                </Col>
                                                <Col sm />
                                                <Col sm align="right">
                                                    <Button onClick={() => this.add(!isLike ? movie : movie.id)} variant="danger">
                                                        <span className={isLike ? "glyphicon glyphicon-minus" : "glyphicon glyphicon-plus"} />
                                                    </Button>
                                                </Col>
                                            </Row>
                                        </Card.Footer>
                                    </Card>
                                )
                            }

                            )
                            :
                            <div>
                                <text>No Add List</text>
                            </div>
                    }
                </CardColumns>
            </div>
        );
    }
}

const mapStateToProps = state => {
    return {
        // movies: state.movies.movies,
        // page: null

    };
};

const mapDispatchToProps = dispatch => {
    return {
        // getMovies: page => dispatch(getMovies(page)),
        // addFavorites: id => dispatch(addFavorites(id))
    };
};

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(AddList);
