import React, { Component } from "react";
import { Row, Col, Button, CardColumns, Card } from "react-bootstrap";
import "../css/list.css";
import { getMovies } from "../actions";
import { addFavorites, removeFavorites } from "../actions/movieActions";

import { connect } from "react-redux";

class MovieList extends Component {
  state = {
    movies: [],
    searchInfo: {},
    page: 1,
    onBOttom: true
  };
  componentDidMount() {
    this.props.getMovies(this.state.page);
    window.addEventListener("scroll", this.handleScroll);
  }

  handleScroll = () => {
    const windowHeight =
      "innerHeight" in window
        ? window.innerHeight
        : document.documentElement.offsetHeight;
    const body = document.body;
    const html = document.documentElement;
    const docHeight = Math.max(
      body.scrollHeight,
      body.offsetHeight,
      html.clientHeight,
      html.scrollHeight,
      html.offsetHeight
    );
    const windowBottom = windowHeight + window.pageYOffset;
    if (windowBottom >= docHeight) {
      this.setState({
        onBOttom: true
      });
    } else {
      this.setState({
        onBOttom: false
      });
    }
  }
  componentWillUnmount() {
    window.removeEventListener("scroll", this.handleScroll);
  }

  componentWillReceiveProps(newProps) {
    if (newProps.movies) {
      this.setState({
        movies: [...this.state.movies, ...newProps.movies],
        page: this.state.page + 1,
        onBOttom: false
      });
    }
  }

  componentDidUpdate() {
    if (this.state.onBOttom) {
      this.props.getMovies(this.state.page);
    }
  }

  setItem = (movie) => {
    console.log(movie, '*******');
    let getItem = localStorage.getItem('favourite');
    if (getItem) {
      let cloneArr = JSON.parse(getItem);
      cloneArr.push(movie);

      if (movie && movie.name) {


        localStorage.setItem('favourite', JSON.stringify(cloneArr))
        this.forceUpdate();
      } else {
        cloneArr.map((item, index) => {
          if (item.id === movie) {
            cloneArr.slice(index)
            console.log(item.name , index);

          }
        })
      }
    }
    else {
      let arr = [movie];
      localStorage.setItem('favourite', JSON.stringify(arr));

      this.forceUpdate();
    }
  }
  render() {
    const { movies, page, searchInfo } = this.state;
    console.log(this.state);
    if (this.props.movies.length === 0) {
      return <h1>Loading...</h1>;
    }

    return (
      <CardColumns id="card-columns">
        {movies.map((movie, index) => {
          const isFavor = JSON.parse(localStorage.getItem('favourite')) ? JSON.parse(localStorage.getItem('favourite')).find(arr => arr.id === movie.id) : false
          // console.log(isFavor,"lalalal")
          return (
            <Card key={movie.id}>
              <Card.Img
                variant="top"
                src={`https://image.tmdb.org/t/p/w185${movie.backdrop_path}`}
              />
              <Card.Body>
                <Card.Title>
                  <h4>{movie.name}</h4>
                  <h6>{movie.vote_average || "No Rating"} </h6>
                </Card.Title>
                <Card.Text>{movie.overview}</Card.Text>
              </Card.Body>
              <Card.Footer>
                <Row>
                  <Col sm>
                    <Button onClick={() => this.setItem(!isFavor ? movie : movie.id)} variant="danger">
                      <span className={isFavor ? "glyphicon glyphicon-heart-empty" : "glyphicon glyphicon-heart"} />
                    </Button>
                  </Col>
                  <Col sm />
                  <Col sm align="right">
                    <Button variant="danger">
                      <span className="glyphicon glyphicon-plus" />
                    </Button>
                  </Col>
                </Row>
              </Card.Footer>
            </Card>
          )
        }

        )}
      </CardColumns>
    );
  }
}

const mapStateToProps = state => {
  return {
    movies: state.movies.movies,
    page: null
  };
};

const mapDispatchToProps = dispatch => {
  return {
    getMovies: page => dispatch(getMovies(page)),
    addFavorites: id => dispatch(addFavorites(id))
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(MovieList);
