const initialState = {
    movies: [],
    movie: {}
}

export default (state = initialState, action) => {
    switch (action.type) {
        case 'GET_MOVIES_SUCCESS':
            return {...state, movies: action.payload}

        case 'FETCH_MOVIE_SUCCESS':
            return {...state, movie: action.payload}


        default:
            return state
    }
}
