import {
    ADD_FAVORITES,
    REMOVE_FAVORITES,
    MOVIE_DETAIL,
    LOAD_FAVORITES,
} from '../utils/index.js';

export function addFavorites(id) {
    return (dispatch, getState) => {
        const moviesList = getState().movies.moviesList;
        return dispatch({
            type: ADD_FAVORITES,
            moviesList,
            id,
        });
    };
}



export function removeFavorites(id) {
    return (dispatch, getState) => {
        const moviesList = getState().movies.moviesList;
        return dispatch({
            type: REMOVE_FAVORITES,
            moviesList,
            id,
        });
    };
}

// export function filmDetail(id) {
