//         type: FILM_DETAIL,
//         id,
//     };
// }

export function getMovies(movies) {
    return {
        type: 'GET_MOVIES_SUCCESS',
        payload: movies

    };
}

export function loadFavorites() {
    return {
        type: LOAD_FAVORITES,
    };
}


export function searchData(val) {
    // console.log(val, 'reducer')
    return dispatch => {
        dispatch(
            { type: "SEARCH", payload: val }
        )
    }
}
